jQuery(function($){var pageId=document.getElementById('imo-options');var instructionsTitle=pageId.getElementsByClassName('imo-instructions');var instructionsList=pageId.getElementsByTagName('ol');var collapsibleTrigger=pageId.getElementsByClassName('imo-collapsible');var collapsibleElement=document.getElementById('imo-animations').getElementsByClassName('form-table');var info=pageId.getElementsByClassName('imo-info');var items=pageId.getElementsByClassName('imo-items');var animationPreview=pageId.getElementsByClassName('preview-button');var i;instructionsList[0].style.display="none";instructionsTitle[0].addEventListener("click",function(){this.classList.toggle("active");if(instructionsList[0].style.display==="none"){$('.imo-instructions + ol').slideDown()}else{$('.imo-instructions + ol').slideUp()}});for(i=0;i<collapsibleTrigger.length;i++){collapsibleElement[i].style.display="none";if(items[i].innerHTML==""){info[i].innerHTML=imoanimPhp.empty}else{info[i].innerHTML=imoanimPhp.selected+items[i].innerHTML}
collapsibleTrigger[i].addEventListener("click",function(){this.classList.toggle("active");var content=this.parentNode.nextElementSibling;var jqContent=$(content);if(content.style.display==="none"){jqContent.slideDown()}else{jqContent.slideUp()}});animationPreview[i].addEventListener("click",function(){var exampleBlock=this.parentNode.parentNode.parentNode.parentNode.querySelector('.imo-example-area .example-block');var animationType=this.parentNode.parentNode.parentNode.parentNode.querySelector('.imo-animation-type').value;var animationDuration=this.parentNode.parentNode.parentNode.parentNode.parentNode.querySelector('.imo-animation-duration').value;var animationRepetition=this.parentNode.parentNode.parentNode.parentNode.parentNode.querySelector('.imo-animation-repetition').value;var animationTiming=this.parentNode.parentNode.parentNode.parentNode.parentNode.querySelector('.imo-animation-timing').value;var animationDelay=this.parentNode.parentNode.parentNode.parentNode.parentNode.querySelector('.imo-animation-delay').value;exampleBlock.style.WebkitAnimationName=animationType;exampleBlock.style.animationName=animationType;exampleBlock.style.WebkitAnimationDuration=animationDuration;exampleBlock.style.animationDuration=animationDuration;exampleBlock.style.WebkitAnimationIterationCount=animationRepetition;exampleBlock.style.animationIterationCount=animationRepetition;exampleBlock.style.WebkitAnimationTimingFunction=animationTiming;exampleBlock.style.animationTimingFunction=animationTiming;exampleBlock.style.WebkitAnimationDelay=animationDelay;exampleBlock.style.animationDelay=animationDelay;var durationTimeout=parseFloat(animationDuration,10);var delayTimeout=parseFloat(animationDelay,10);if(animationRepetition!=='infinite'){var overallTimeout=(durationTimeout+delayTimeout)*1000*animationRepetition}
else{var overallTimeout=(durationTimeout+delayTimeout)*1000*10}
setTimeout(function(){exampleBlock.style.animationName=''},overallTimeout)})}})